import { Component, EventEmitter, Input, Output } from "@angular/core";
import { Cidade } from "@domain/cidade";
import { ProjetoService } from "@service/projeto-service";
import { MessageService } from "primeng/api";

import { DialogModule } from "primeng/dialog";
import { ButtonModule } from "primeng/button";
import { InputTextModule } from "primeng/inputtext";
import { RadioButtonModule } from "primeng/radiobutton";
import { FormsModule } from "@angular/forms";

@Component({
    selector: "cadastrar-cidade",
    templateUrl: "cadastrar-cidade.html",
    standalone: true,
    imports: [DialogModule],
    providers: [ProjetoService, MessageService],
})
export class CadastrarCidade {
    @Input() public cidade: Cidade = new Cidade();
    @Output("onClose") private eventoFechaJanela = new EventEmitter<boolean>();

    constructor(
        private service: ProjetoService,
        private messageService: MessageService
    ) {}

    public salvar(): void {
        this.service.salvar(this.cidade).subscribe({
            next: () => {
                this.messageService.add({
                    severity: "success",
                    summary: "Info",
                    detail: `Cidade '${this.cidade.nome}' cadastrada com sucesso!`,
                });
                this.cancelar();
            },
            error: () => {
                this.messageService.add({
                    severity: "error",
                    summary: "Erro",
                    detail: `Erro ao salvar a cidade!`,
                });
            },
        });
    }

    public cancelar(): void {
        this.eventoFechaJanela.emit(true);
    }
}
<p-dialog header="Cadastrar Cidade" [(visible)]="cidade" [modal]="true" [style]="{ width: '25rem' }">
    <div class="flex align-items-center gap-3 mb-3">
        <label for="nome" class="font-semibold w-6rem">Nome</label>
        <input pInputText id="nome" [(ngModel)]="cidade.nome" class="flex-auto" autocomplete="off" />
    </div>

    <div class="flex align-items-center gap-3 mb-3">
        <label for="uf" class="font-semibold w-6rem">UF</label>
        <input pInputText id="uf" class="flex-auto" [(ngModel)]="cidade.uf" autocomplete="off" />
    </div>

    <div class="flex align-items-center gap-3 mb-5">
        <label class="font-semibold w-6rem">Capital</label>
        <p-radioButton name="capital" [value]="true" [(ngModel)]="cidade.capital" inputId="op1" />
        <label for="op1" class="ml-2">Sim</label>

        <p-radioButton name="capital" [value]="false" [(ngModel)]="cidade.capital" inputId="op2" />
        <label for="op2" class="ml-2">Não</label>
    </div>

    <div class="flex justify-content-end gap-2">
        <p-button label="Cancelar" severity="secondary" (click)="cancelar()" />
        <p-button label="Salvar" (click)="salvar()" />
    </div>
</p-dialog>
